CREATE TABLE DOCUMENT (ID INTEGER AUTO_INCREMENT NOT NULL, LAST_MODIFIED_BY INTEGER, NAME VARCHAR(45) NOT NULL, STATUS VARCHAR(45) NOT NULL, URL VARCHAR(2000) NOT NULL, DOCUMENTTYPE_ID INTEGER NOT NULL, USER_ID INTEGER NOT NULL, PRIMARY KEY (ID))
CREATE TABLE DOCUMENT_TYPE (ID INTEGER AUTO_INCREMENT NOT NULL, TYPE_NAME VARCHAR(45) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE TOPIC (ID INTEGER AUTO_INCREMENT NOT NULL, CREATE_TIME DATETIME, DESCRIPTION VARCHAR(1000), NAME VARCHAR(500) NOT NULL, OUTSIDER VARCHAR(500), PASS_STATUS VARCHAR(45), TEACHER_COMMENT VARCHAR(1000), UPDATE_TIME DATETIME, TOPICTYPE_ID INTEGER NOT NULL, PRIMARY KEY (ID))
CREATE TABLE TOPIC_TYPE (ID INTEGER AUTO_INCREMENT NOT NULL, TYPE_NAME VARCHAR(45) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE USER (ID INTEGER AUTO_INCREMENT NOT NULL, ROLE VARCHAR(31), ACCOUNT VARCHAR(45) NOT NULL, CREATE_TIME DATETIME, EMAIL VARCHAR(1000), GENDER VARCHAR(45), GPA VARCHAR(45), NAME VARCHAR(45) NOT NULL, PASSWORD VARCHAR(45) NOT NULL, PHONE VARCHAR(1000), SELF_DESCRIPTION VARCHAR(1000), UPDATE_TIME DATETIME, TEACHER_ID INTEGER, CAPACITY INTEGER, DEGREE VARCHAR(45), TITLE VARCHAR(45), PRIMARY KEY (ID))
CREATE TABLE MENU (ID INTEGER AUTO_INCREMENT NOT NULL, NAME VARCHAR(45) NOT NULL, PARENTID INTEGER, ROLE VARCHAR(45) NOT NULL, URL VARCHAR(2000) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE WILL (CREATE_TIME DATETIME, LEVEL INTEGER NOT NULL, UPDATE_TIME DATETIME, STUDENTID INTEGER NOT NULL, TEACHERID INTEGER NOT NULL, PRIMARY KEY (STUDENTID, TEACHERID))
ALTER TABLE DOCUMENT ADD CONSTRAINT FK_DOCUMENT_DOCUMENTTYPE_ID FOREIGN KEY (DOCUMENTTYPE_ID) REFERENCES DOCUMENT_TYPE (ID)
ALTER TABLE DOCUMENT ADD CONSTRAINT FK_DOCUMENT_USER_ID FOREIGN KEY (USER_ID) REFERENCES USER (ID)
ALTER TABLE TOPIC ADD CONSTRAINT FK_TOPIC_TOPICTYPE_ID FOREIGN KEY (TOPICTYPE_ID) REFERENCES TOPIC_TYPE (ID)
ALTER TABLE USER ADD CONSTRAINT FK_USER_TEACHER_ID FOREIGN KEY (TEACHER_ID) REFERENCES USER (ID)
