CREATE TABLE document (ID INTEGER NOT NULL, CREATE_TIME DATETIME, LAST_MODIFIED_BY INTEGER, NAME VARCHAR(45) NOT NULL, STATUS VARCHAR(45) NOT NULL, UPDATE_TIME DATETIME, URL VARCHAR(2000) NOT NULL, DOCUMENTTYPE_ID INTEGER NOT NULL, USER_ID INTEGER NOT NULL, PRIMARY KEY (ID))
CREATE TABLE documenttype (ID INTEGER NOT NULL, TYPE_NAME VARCHAR(45) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE topic (ID INTEGER NOT NULL, CREATE_TIME DATETIME, DESCRIPTION VARCHAR(1000), NAME VARCHAR(500) NOT NULL, OUTSIDER VARCHAR(500), PASS_STATUS VARCHAR(45), TEACHER_COMMENT VARCHAR(1000), TOPICTYPE_ID INTEGER NOT NULL, PRIMARY KEY (ID))
CREATE TABLE topictype (ID INTEGER NOT NULL, TYPE_NAME VARCHAR(45) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE user (ID INTEGER NOT NULL, ACCOUNT VARCHAR(45) NOT NULL, CAPACITY INTEGER, CREATE_TIME DATETIME, DEGREE VARCHAR(45), EMAIL VARCHAR(1000), GENDER VARCHAR(45), GPA VARCHAR(45), NAME VARCHAR(45) NOT NULL, PASSWORD VARCHAR(45) NOT NULL, PHONE VARCHAR(1000), ROLE VARCHAR(45) NOT NULL, SELF_DESCRIPTION VARCHAR(1000), TITLE VARCHAR(45), MENTOR_ID INTEGER, PRIMARY KEY (ID))
ALTER TABLE document ADD CONSTRAINT FK_document_USER_ID FOREIGN KEY (USER_ID) REFERENCES user (ID)
ALTER TABLE document ADD CONSTRAINT FK_document_DOCUMENTTYPE_ID FOREIGN KEY (DOCUMENTTYPE_ID) REFERENCES documenttype (ID)
ALTER TABLE topic ADD CONSTRAINT FK_topic_TOPICTYPE_ID FOREIGN KEY (TOPICTYPE_ID) REFERENCES topictype (ID)
ALTER TABLE user ADD CONSTRAINT FK_user_MENTOR_ID FOREIGN KEY (MENTOR_ID) REFERENCES user (ID)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(38), PRIMARY KEY (SEQ_NAME))
